<?php  $headers = getHeaders2();  echo nl2br(print_r($headers));  function getHeaders2 ()  {    $headers = array();    $keys    = preg_grep('{^HTTP_}i', array_keys($_SERVER));    foreach ($keys as $val) {      $key           = str_replace(' ', '-', ucwords(strtolower(str_replace('_', ' ', substr($val, 5)))));      $headers[$key] = $_SERVER[$val];    }    return $headers;  }